# 不需要去掉停用词

# 项目的根目录
project_root = os.path.dirname(os.path.dirname(os.path.abspath(__file__)))


# 数据预处理
def data_process(data):
    stopwords = codecs.open('dict/stopword.txt', 'r', encoding='utf8').readlines()
    stopwords = [w.strip() for w in stopwords]
    test_doc = []
    for line in data:
        line = jieba.lcut(line.strip())
        a=[]
        a.extend(w for w in line if w not in stopwords)
        test_doc.append(a)
    return test_doc


# 针对json格式的文本再次处理
def get_dict():
    docs = []
    # id到案号的映射
    id2case = dict()
    # 案号到id的映射
    case2id = dict()

    f = open("../Text_Similarity/data/train_data.json", 'r', encoding='utf8')
    for idx, line in enumerate(f):
        if idx % 1000 == 0:
            print('已完成：{}'.format(idx))
        # 转换为python字典
        line: Dict = json.loads(line)
        # 案号
        key = line["id"]
        id2case[len(id2case)] = key
        case2id[key] = len(case2id)

        docs.append(line["fullText"])

        # 持久化两个字典文件
        # 持久化两个字典文件
        f = open(os.path.join(project_root, "textSimilarity/dict/id2case.json"), 'w', encoding='utf8')
        f1 = open(os.path.join(project_root, "textSimilarity/dict/case2id.json"), 'w', encoding='utf8')
        f.write(json.dumps(id2case, ensure_ascii=False, indent=4))
        f1.write(json.dumps(case2id, ensure_ascii=False, indent=4))

    results = data_process(docs)
    return results

def train_model():
    train_set=get_dict()

    documents = [TaggedDocument(doc, [i]) for i, doc in enumerate(train_set)]

    model = Doc2Vec(documents, vector_size=5, window=2, min_count=1, workers=4)
    print("训练结束")
    model.save("my_doc2vec_model")

